import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { register } from '../redux/slices/authSlice';
import { useNavigate } from 'react-router-dom';
export default function Register(){
  const [form,setForm] = useState({ name:'', email:'', password:'', role:'employee', department:''});
  const dispatch = useDispatch(); const navigate = useNavigate();
  const submit = async (e)=>{ e.preventDefault(); await dispatch(register(form)); navigate('/dashboard'); };
  return (
    <div style={{display:'flex',alignItems:'center',justifyContent:'center',height:'80vh'}}>
      <div style={{width:460,padding:30,background:'#fff',borderRadius:8}}>
        <h2>Create Account</h2>
        <form onSubmit={submit}>
          <input className="input" placeholder="Full name" value={form.name} onChange={e=>setForm({...form,name:e.target.value})}/>
          <input className="input" placeholder="Email" value={form.email} onChange={e=>setForm({...form,email:e.target.value})}/>
          <select className="input" value={form.department} onChange={e=>setForm({...form,department:e.target.value})}>
            <option value="">Select department</option>
            <option>Engineering</option><option>Marketing</option><option>Sales</option><option>HR</option>
          </select>
          <select className="input" value={form.role} onChange={e=>setForm({...form,role:e.target.value})}>
            <option value="employee">Employee</option><option value="manager">Manager</option>
          </select>
          <input className="input" type="password" placeholder="Password" value={form.password} onChange={e=>setForm({...form,password:e.target.value})}/>
          <button className="btn" style={{width:'100%'}}>Create Account</button>
        </form>
      </div>
    </div>
  );
}
